mapper.select:
select a.customernumber, a.counsellingdate, a.counsellingtime, a.category, (select codename from codegroupdetail z where z.codegroupid = 'CATEGORY' and z.code = a.category) as categoryname, a.connumber, a.title, a.state, (select codename from codegroupdetail z where z.codegroupid = 'COUNSELSTATE' and z.code = a.state) as statename, date_format(a.auditdatetime,'%Y%m%d%H%i') as auditdatetime, b.customername, b.phonenumber, nvl((select f.employeename from login e, employee f where e.loginid = a.auditid and e.employeenumber = f.employeenumber),a.auditid) as employeename, sum(1) over() as totalcount from counsellinghistory a, customer b where a.counsellingdate between #{startDate} and #{endDate}
and a.state = #{state}
and a.state != 'DELETE'
and a.connumber = #{conNumber}
and a.category = #{category}
and a.title like concat('%',#{title},'%')
and a.customernumber = b.customernumber and b.providernumber = #{providerNumber}
and b.customername like concat(#{customerName},'%')
and b.phonenumber like concat(#{phoneNumber},'%')
order by a.counsellingdate desc, a.counsellingtime desc, b.customername
limit #{pageStart}, #{perPageNum}

mapper.update:
update counsellinghistory a set a.category = #{category}, a.inboundpath = #{inboundPath}, a.state = #{state}, a.connumber = #{conNumber},
                          a.title = #{title}, a.memo = #{memo}, a.answer = #{answer}, a.auditid = #{auditId}, a.auditdatetime = now()
where  a.customernumber = #{customerNumber}
and    a.counsellingdate = #{counsellingDate}
and    a.counsellingtime = #{counsellingTime}

mapper.delete:
update counsellinghistory set state = 'DELETE', auditId = #{auditId}, auditdatetime = now()
where  customernumber = #{customerNumber}
and    counsellingdate = #{counsellingDate}
and    counsellingtime = #{counsellingTime}
