mapper.insert:
INSERT 
INTO   address
       (
          addressid
		    , zipcode
		    , baseaddress
		    , detailaddress
		    , auditid
		    , auditdatetime
		  )
VALUES (
          #{addressId}
        , #{zipcode}
        , #{baseAddress}
        , #{detailAddress}
        , 'TEST'
        , NOW()
        )

mapper.update:
UPDATE address 
SET    zipcode = #{zipcode}
	   , baseaddress = #{baseAddress}
	   , detailaddress = #{detailAddress}
	   , auditid = 'TEST'
	   , auditdatetime = NOW()  
WHERE  addressid = #{addressId}

UPDATE customer 
SET    customername = #{customerName}
     , sex = #{sex}
	   , birthday = #{birthday}
	   , email = #{email}
	   , phonenumber = #{phoneNumber}
	   , addressid = #{addressId}
	   , deliveryaddressid = ''
	   , currentdate = DATE_FORMAT(NOW(), '%Y%m%d')
	   , auditid = 'TEST'
	   , auditdatetime = NOW()  
WHERE  customernumber = #{customerNumber}
