mapper.select:
select  nvl(max(channelcontractnumber),0)+1 as channelcontractnumber
from    channelcontract a
where   a.providernumber = #{providernumber}
and     a.channelid = #{channelid}
and     a.productordernumber = #{productordernumber}
and     a.ordernumber = #{ordernumber}

select customernumber
from   customer
where  customername = #{deliverycustomername}
and    cellphonenumber = #{deliveryphonenumber1} 
and    phonenumber = #{deliveryphonenumber2}

select deliverydate, deliverydate as deliverystartdatetime, date_format(str_to_date(deliverydate,'%Y%m%d') + interval (12-1) * 2 month,'%Y%m%d') as effectenddatetime
from  (select if(date_format(curdate(),'%d')>cast(#{deliveryday} as unsigned),concat(date_format(curdate()+interval 1 month,'%Y%m'),if(cast(#{deliveryday} as unsigned)>9,#{deliveryday},concat('0',#{deliveryday}))),
                                                               concat(date_format(curdate(),'%Y%m'),if(cast(#{deliveryday} as unsigned)>9,#{deliveryday},concat('0',#{deliveryday})))) as deliverydate) a

select b.connumber from channelcontract a, contract b where a.providernumber = #{providernumber}
and a.channelid = #{channelid}
and a.registrationdatetime like concat(#{registrationdatetime},'%') and a.providernumber = b.providernumber and a.channelid = b.channelid and a.productordernumber = b.channelproductordernumber and a.ordernumber = b.channelordernumber and a.channelcontractnumber = b.channelcontractnumber

select a.connumber, a.providernumber, a.channelid, a.channelproductordernumber as productordernumber, a.channelordernumber as ordernumber, a.channelcontractnumber, a.paymentinformationnumber, 
       if(max(if(b.deliverystate='DONE',1,0))=1,'DONE','') as deliverystate    
from   contract a left outer join deliverydetail b on (a.connumber = b.connumber)
where  a.connumber = #{connumber}
group by a.connumber, a.providernumber, a.channelid, a.channelproductordernumber, a.channelordernumber, a.channelcontractnumber, a.paymentinformationnumber

mapper.delete:
delete from channelcontract
where  providernumber = #{providernumber}
and    channelid = #{channelid}
and    productordernumber = #{productordernumber}
and    ordernumber = #{ordernumber}
and    channelcontractnumber = #{channelcontractnumber}

mapper.select:
select b.connumber, b.recurringdeliveryyn, substr(b.registrationdatetime,1,8) AS registrationdatetime, substr(b.subscribedatetime,1,8) as subscribedatetime, b.channelid, (select codename from codegroupdetail z where z.codegroupid = 'CHANNEL' and z.code = b.channelid) as channelname, b.channelproductordernumber as productordernumber, b.channelordernumber as ordernumber, b.channelcontractnumber, b.deliverychargetype, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYCHARGETYPE' and z.code = b.deliverychargetype),'') as deliverychargetypename, b.deliverychargeamount, b.deliveryremark, b.deliverystartdatetime, b.effectenddatetime, b.duration, b.deliverycycle, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYCYCLE' and z.code = b.deliverycycle),'') as deliverycyclename, b.deliverytimes, b.deliveryday, b.deliverytype, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYTYPE' and z.code = b.deliverytype),'') as deliverytypename, b.deliverytotalcount, (select count(*) from deliverydetail y where y.connumber = b.connumber and y.deliverydate < date_format(now(),'%Y%m%d')) + b.deliverycurrentcount as currentdeliverycount, c.productname, c.quantity, c.productoption, c.productdescription1, c.productdescription2, c.productdescription3, c.productdescription4, c.productdescription5, c.bigissuequantity, d.customername, d.phonenumber as customerphonenumber, nvl(b.customertype,b.channelkind) as customertype, e.customername as deliverycustomername, nvl(e.cellphonenumber, e.phonenumber) as deliveryphonenumber1, f.zipcode as deliveryzipcode, f.baseaddress as deliveryaddress, g.paymentday, g.paymentmethod, nvl((select codename from codegroupdetail z where z.codegroupid = 'PAYMENTMETHODCODE' and z.code = g.paymentmethod),'') as paymentmethodname, g.paymentamount, (select count(*) from deliverydetail y where y.connumber = b.connumber and y.deliverydate < date_format(now(),'%Y%m%d')) as deliveryindex2, sum(1) over() totalcount from contract b, contractproduct c, customer d, customer e, address f, paymentinformation g where b.providernumber = #{providernumber} and b.registrationdatetime between concat(#{registrationdatetime},'000000') and concat(#{registrationdatetime},'235959')
and b.channelid = #{channelid}
and b.connumber = c.connumber and b.customernumber = d.customernumber and b.deliverycustomernumber = e.customernumber and b.deliveryaddressid = f.addressid and b.paymentinformationnumber = g.paymentinformationnumber order by substr(b.registrationdatetime,1,8), b.recurringdeliveryyn desc, deliverytypename, channelname, d.customername limit #{pageStart}, #{perPageNum}

select a.deliverydate, a.deliverycompany, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYCOMPANY' and z.code = a.deliverycompany),'') as deliverycompanyname, a.deliverycustomernumber, a.deliverystate, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYSTATE' and z.code = a.deliverystate),'') as deliverystatename, a.deliveryremark, b.productname, b.quantity, c.customername, nvl(c.cellphonenumber,c.phonenumber) as customerphonenumber, d.zipcode, d.baseaddress as deliveryaddress, e.channelid, (select codename from codegroupdetail z where z.codegroupid = 'CHANNEL' and z.code = e.channelid) as channelname, e.channelordernumber as ordernumber, e.contractstate, (select codename from codegroupdetail z where z.codegroupid = 'CONTRACTSTATE' and z.code = e.contractstate) as contractstatename, sum(1) over() totalcount from deliverydetail a, contractproduct b, customer c, address d, contract e where a.providernumber = #{providernumber} and a.deliverydate between #{deliverydate1} and #{deliverydate2}
and a.deliverycompany = #{deliverycompany}
and a.connumber = b.connumber and a.deliverycustomernumber = c.customernumber and a.deliveryaddressid = d.addressid and a.connumber = e.connumber
and e.recurringdeliveryyn = #{recurringdeliveryyn}
and (e.contractstate = 'ACTIVATION' or a.deliverystate = 'DONE') order by a.deliverydate, deliverycompanyname, c.customername
limit #{pageStart}, #{perPageNum}

mapper.update:
update deliverydetail a set a.deliverystate = 'DONE', a.deliverydatetime = now(), a.auditid = #{auditid}, a.auditdatetime = now()
where  a.providernumber = #{providernumber}
and    a.deliverydate = #{deliverydate}
and    a.deliverycompany = #{deliverycompany}
and    a.deliverystate = 'BEFORE'

update deliverydetail a set a.deliverydate = #{deliverydate}, a.deliveryremark = #{deliveryremark}, a.deliverystate = #{deliverystate}, a.deliveryaddressid = #{deliveryaddressid}, 
                      a.auditid = #{auditid}, a.auditdatetime = now()
where  deliverynumber = #{deliverynumber}

mapper.select:
select a.customernumber, customername, cellphonenumber as deliveryphonenumber1, phonenumber as deliveryphonenumber2 from customer a where a.providernumber = #{providernumber} and a.customername like concat(#{customername},'%')
and (a.phonenumber like concat(#{customerphonenumber},'%') or a.cellphonenumber like concat(#{customerphonenumber},'%'))
limit #{pageStart}, #{perPageNum}

select a.customernumber, a.counsellingdate, a.counsellingtime, a.category, a.memo, a.inboundpath, a.answer, a.state
from   counsellinghistory a
where  a.customernumber = #{customernumber}
and    a.counsellingdate = #{counsellingdate}
and    a.counsellingtime = #{counsellingtime}

mapper.update:
update counsellinghistory a set a.category = #{category}, a.inboundpath = #{inboundpath}, a.memo = #{memo}, a.answer = #{answer}, a.state = #{state}, a.auditid = #{auditid}, a.auditdatetime = now()
where  a.customernumber = #{customernumber}
and    a.counsellingdate = #{counsellingdate}
and    a.counsellingtime = #{counsellingtime}

mapper.select:
select b.connumber, b.recurringdeliveryyn, substr(b.registrationdatetime,1,8) AS registrationdatetime, substr(b.subscribedatetime,1,8) as subscribedatetime, b.channelid, (select codename from codegroupdetail z where z.codegroupid = 'CHANNEL' and z.code = b.channelid) as channelname, b.deliverychargetype, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYCHARGETYPE' and z.code = b.deliverychargetype),'') as deliverychargetypename, b.deliverychargeamount, b.deliveryremark, b.deliverystartdatetime, b.effectenddatetime, b.duration, b.deliverycycle, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYCYCLE' and z.code = b.deliverycycle),'') as deliverycyclename, b.deliverytimes, b.deliveryday, b.deliverytype, nvl((select codename from codegroupdetail z where z.codegroupid = 'DELIVERYTYPE' and z.code = b.deliverytype),'') as deliverytypename, b.deliverytotalcount, (select count(*) from deliverydetail y where y.connumber = b.connumber and y.deliverydate < date_format(now(),'%Y%m%d')) + b.deliverycurrentcount as currentdeliverycount, b.contractstate, nvl((select codename from codegroupdetail z where z.codegroupid = 'CONTRACTSTATE' and z.code = b.contractstate),'') as contractstatename, c.productname, c.quantity, c.productoption, c.productdescription1, c.productdescription2, c.productdescription3, c.productdescription4, c.productdescription5, c.bigissuequantity, d.customername, d.phonenumber as customerphonenumber, nvl(b.customertype,b.channelkind) as customertype, e.customername as deliverycustomername, nvl(e.cellphonenumber, e.phonenumber) as deliveryphonenumber1, f.zipcode as deliveryzipcode, f.baseaddress as deliveryaddress, g.paymentday, g.paymentmethod, nvl((select codename from codegroupdetail z where z.codegroupid = 'PAYMENTMETHODCODE' and z.code = g.paymentmethod),'') as paymentmethodname, g.paymentamount, (select count(*) from deliverydetail y where y.connumber = b.connumber and y.deliverydate < date_format(now(),'%Y%m%d')) as deliveryindex2, sum(1) over() totalcount from contract b, contractproduct c, customer d, customer e, address f, paymentinformation g where b.providernumber = #{providernumber}
and b.contractstate != 'ACTIVATION'
and b.contractstate = #{contractstate}
and b.recurringdeliveryyn = #{recurringdeliveryyn}
and (b.effectenddatetime like concat(#{effectenddatetime},'%') or b.suspenddatetime like concat(#{effectenddatetime},'%'))
and b.connumber = c.connumber and b.customernumber = d.customernumber and b.deliverycustomernumber = e.customernumber and b.deliveryaddressid = f.addressid and b.paymentinformationnumber = g.paymentinformationnumber order by substr(b.registrationdatetime,1,8), b.recurringdeliveryyn desc, deliverytypename, channelname, d.customername
limit #{pageStart}, #{perPageNum}
